    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String uname = this.jTextField1.getText();
        String pass = this.jPasswordField1.getText();
        if (uname.equals("") && pass.equals("")) {
            JOptionPane.showMessageDialog(this.rootPane, "ENTER UR USERNAME AND PASSWORD");
        } else {
            try {
                Class.forName("oracle.jdbc.driver.OracleDriver");
                Connection conn=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:XE","hr","oracle");
                Statement stmt = conn.createStatement();
                String query = "select pass from userlogin where uname='" + uname + "'";
                System.out.println(query);
                ResultSet rsl = stmt.executeQuery(query);
                if (rsl.next()) {
                    String password = rsl.getString(1);
                    if (password.equals(pass)) {
                        JOptionPane.showMessageDialog(this.rootPane, "Login Successful! ");
                        welcome a = new welcome();
                        a.show();
                        this.hide();
                    } else {
                        JOptionPane.showMessageDialog(this.rootPane, "Login Unsuccessful!");
                    }
                }
            }
            catch (Exception e) {
                JOptionPane.showMessageDialog(this.rootPane, "login" + e);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed
    
